import { Component, Input, OnInit } from '@angular/core';
import { User } from '../../interfaces/user';
import { Resume } from '../../interfaces/resume';

@Component({
  selector: 'appResume',
  templateUrl: './resume.component.html',
  styleUrls: ['./resume.component.scss']
})
export class ResumeComponent implements OnInit {

  @Input() resume: Resume;

  constructor(){}

  ngOnInit(): void {
  }
}
<div class="w-full h-full flex justify-start items-center">
    <div class="w-[17rem] bg-gray-300 min-h-[100%]">
        <div class="w-full mb-[3.5rem] relative p-5 py-10 flex flex-col justify-start items-center h-[13rem] bg-blue-500 gap-4 rounded-b-[4rem]">
            <h1 class="w-full flex justify-center items-center text-md font-semibold text-gray-50 capitalize">{{resume?.user?.firstName}} <span class="uppercase">{{resume?.user?.lastName}}</span></h1>
            <div class=" absolute bottom-[-20%] flex items-center rounded-full w-[10rem]">
                <img src="../../../../assets/images/profile.png" alt="Profile" class="w-full h-full rounded-full" />
            </div>
        </div>
        <div class="w-full h-fullflex flex-col justify-start items-start gap-5 p-4">
            <h1 class="w-full text-md font-bold text-blue-500">Informations personnelles</h1>
            <hr class="border-gray-400 my-3">

        </div>
    </div>

</div>
